import React, { useState } from 'react';
import {
  Container,
  TextField,
  Button,
  Paper,
  Typography,
  Box,
  CircularProgress,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import axios from 'axios';

function App() {
  const [prompt, setPrompt] = useState('');
  const [numGuests, setNumGuests] = useState(1);
  const [script, setScript] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleGenerateScript = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/generate-script', {
        prompt,
        num_guests: numGuests,
      });
      setScript(response.data.script);
    } catch (err) {
      setError('Error generating script: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePodcast = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/create-podcast', {
        script,
      }, {
        responseType: 'blob' // Important: This tells axios to handle binary data
      });

      // Create a blob URL from the audio data
      const audioBlob = new Blob([response.data], { type: 'audio/mp3' });
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudioUrl(audioUrl);
    } catch (err) {
      setError('Error creating podcast: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h3" component="h1" gutterBottom align="center">
        AI Podcast Generator
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          label="Enter your podcast topic"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          sx={{ mb: 2 }}
        />
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Number of Guests</InputLabel>
          <Select
            value={numGuests}
            label="Number of Guests"
            onChange={(e) => setNumGuests(e.target.value)}
          >
            <MenuItem value={1}>1 Guest</MenuItem>
            <MenuItem value={2}>2 Guests</MenuItem>
            <MenuItem value={3}>3 Guests</MenuItem>
          </Select>
        </FormControl>
        <Button
          variant="contained"
          color="primary"
          onClick={handleGenerateScript}
          disabled={loading || !prompt}
          fullWidth
        >
          Generate Script
        </Button>
      </Paper>

      {script && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Generated Script
            </Typography>
            <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
              {script.map(([speaker, line], index) => (
                <div key={index}>
                  <strong>{speaker}:</strong> {line}
                </div>
              ))}
            </Typography>
            <Box sx={{ mt: 2 }}>
              <Button
                variant="contained"
                color="secondary"
                onClick={handleCreatePodcast}
                disabled={loading}
                fullWidth
              >
                Create Podcast
              </Button>
            </Box>
          </CardContent>
        </Card>
      )}

      {audioUrl && (
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Your Podcast
            </Typography>
            <audio 
              controls 
              src={audioUrl} 
              style={{ width: '100%' }}
              controlsList="nodownload"
            />
          </CardContent>
        </Card>
      )}

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
          <CircularProgress />
        </Box>
      )}

      {error && (
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      )}
    </Container>
  );
}

export default App;
