import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Paper,
  Typography,
  Box,
  CircularProgress,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Alert,
  Grid,
  IconButton,
} from '@mui/material';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import StopIcon from '@mui/icons-material/Stop';
import axios from 'axios';

function App() {
  const [prompt, setPrompt] = useState('');
  const [script, setScript] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [numGuests, setNumGuests] = useState(1);
  const [voices, setVoices] = useState([]);
  const [hostVoiceId, setHostVoiceId] = useState('');
  const [guestVoiceId, setGuestVoiceId] = useState('');
  const [playingSample, setPlayingSample] = useState(null);

  useEffect(() => {
    // Fetch available voices when component mounts
    const fetchVoices = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-voices');
        if (Array.isArray(response.data)) {
          setVoices(response.data);
          // Set default voices if available
          if (response.data.length > 0) {
            setHostVoiceId(response.data[0].voice_id);
            setGuestVoiceId(response.data[1]?.voice_id || response.data[0].voice_id);
          }
        } else {
          setError('Invalid voices data received from server');
        }
      } catch (err) {
        setError('Error fetching voices: ' + err.message);
      }
    };
    fetchVoices();
  }, []);

  const playSample = async (voiceId) => {
    try {
      setPlayingSample(voiceId);
      const response = await axios.post('http://localhost:5000/create-podcast', {
        script: 'script = [("Speaker", "This is a sample of my voice.")]',
        hostVoiceId: voiceId,
        guestVoiceId: voiceId
      }, {
        responseType: 'blob'
      });

      const audioBlob = new Blob([response.data], { type: 'audio/mp3' });
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);
      audio.onended = () => setPlayingSample(null);
      audio.play();
    } catch (err) {
      setError('Error playing sample: ' + err.message);
      setPlayingSample(null);
    }
  };

  const handleGenerateScript = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/generate-script', {
        prompt,
        numGuests
      });
      setScript(response.data.script);
    } catch (err) {
      const errorMessage = err.response?.data?.error || err.message;
      setError(`Error generating script: ${errorMessage}`);
      console.error('Script generation error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePodcast = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/create-podcast', {
        script,
        hostVoiceId,
        guestVoiceId
      }, {
        responseType: 'blob'
      });

      const audioBlob = new Blob([response.data], { type: 'audio/mp3' });
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudioUrl(audioUrl);
    } catch (err) {
      let errorMessage = 'Error creating podcast';
      if (err.response?.data) {
        const reader = new FileReader();
        reader.onload = () => {
          try {
            const errorData = JSON.parse(reader.result);
            setError(`Error creating podcast: ${errorData.error}`);
          } catch (e) {
            setError(`Error creating podcast: ${err.message}`);
          }
        };
        reader.readAsText(err.response.data);
      } else {
        setError(`Error creating podcast: ${err.message}`);
      }
      console.error('Podcast creation error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h3" component="h1" gutterBottom align="center">
        AI Podcast Generator
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          label="Enter your podcast topic"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          sx={{ mb: 2 }}
        />
        <Grid container spacing={2} sx={{ mb: 2 }}>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Number of Guests</InputLabel>
              <Select
                value={numGuests}
                label="Number of Guests"
                onChange={(e) => setNumGuests(e.target.value)}
              >
                <MenuItem value={1}>1 Guest</MenuItem>
                <MenuItem value={2}>2 Guests</MenuItem>
                <MenuItem value={3}>3 Guests</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Host Voice</InputLabel>
              <Select
                value={hostVoiceId}
                label="Host Voice"
                onChange={(e) => setHostVoiceId(e.target.value)}
              >
                {voices.map((voice) => (
                  <MenuItem key={voice.voice_id} value={voice.voice_id}>
                    {voice.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <IconButton 
              onClick={() => playSample(hostVoiceId)}
              disabled={!hostVoiceId || playingSample === hostVoiceId}
            >
              {playingSample === hostVoiceId ? <StopIcon /> : <PlayArrowIcon />}
            </IconButton>
          </Grid>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Guest Voice</InputLabel>
              <Select
                value={guestVoiceId}
                label="Guest Voice"
                onChange={(e) => setGuestVoiceId(e.target.value)}
              >
                {voices.map((voice) => (
                  <MenuItem key={voice.voice_id} value={voice.voice_id}>
                    {voice.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <IconButton 
              onClick={() => playSample(guestVoiceId)}
              disabled={!guestVoiceId || playingSample === guestVoiceId}
            >
              {playingSample === guestVoiceId ? <StopIcon /> : <PlayArrowIcon />}
            </IconButton>
          </Grid>
        </Grid>
        <Button
          variant="contained"
          color="primary"
          onClick={handleGenerateScript}
          disabled={loading || !prompt}
          fullWidth
        >
          Generate Script
        </Button>
      </Paper>

      {script && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Generated Script
            </Typography>
            <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
              {script}
            </Typography>
            <Box sx={{ mt: 2 }}>
              <Button
                variant="contained"
                color="secondary"
                onClick={handleCreatePodcast}
                disabled={loading || !hostVoiceId || !guestVoiceId}
                fullWidth
              >
                Create Podcast
              </Button>
            </Box>
          </CardContent>
        </Card>
      )}

      {audioUrl && (
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Your Podcast
            </Typography>
            <audio 
              controls 
              src={audioUrl} 
              style={{ width: '100%' }}
              controlsList="nodownload"
            />
          </CardContent>
        </Card>
      )}

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
          <CircularProgress />
        </Box>
      )}

      {error && (
        <Alert severity="error" sx={{ mt: 2 }}>
          {error}
        </Alert>
      )}
    </Container>
  );
}

export default App;
