import React, { useState, useEffect } from 'react';
import {
  Container,
  TextField,
  Button,
  Paper,
  Typography,
  Box,
  CircularProgress,
  Card,
  CardContent,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import axios from 'axios';

function App() {
  const [prompt, setPrompt] = useState('');
  const [numGuests, setNumGuests] = useState(1);
  const [script, setScript] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [voices, setVoices] = useState([]);
  const [voiceSelections, setVoiceSelections] = useState({
    Host: "pNInz6obpgDQGcFmaJgB",  // Adam
    Guest: "Xb7hH8MSUJpSbSDYk0k2",  // Alice
  });
  const [showVoiceDialog, setShowVoiceDialog] = useState(false);
  const [currentSpeaker, setCurrentSpeaker] = useState('');

  useEffect(() => {
    // Load voices from the JSON file
    fetch('/voices.json')
      .then(response => response.json())
      .then(data => setVoices(data))
      .catch(error => console.error('Error loading voices:', error));
  }, []);

  const handleGenerateScript = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/generate-script', {
        prompt,
        numGuests,
      });
      setScript(response.data.script);
    } catch (err) {
      setError('Error generating script: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePodcast = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await axios.post('http://localhost:5000/create-podcast', {
        script,
        voiceSelections,
      }, {
        responseType: 'blob'
      });

      const audioBlob = new Blob([response.data], { type: 'audio/mp3' });
      const audioUrl = URL.createObjectURL(audioBlob);
      setAudioUrl(audioUrl);
    } catch (err) {
      setError('Error creating podcast: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleVoiceSelect = (speaker) => {
    setCurrentSpeaker(speaker);
    setShowVoiceDialog(true);
  };

  const handleVoiceChange = (voiceId) => {
    setVoiceSelections(prev => ({
      ...prev,
      [currentSpeaker]: voiceId
    }));
    setShowVoiceDialog(false);
  };

  const renderScript = (scriptText) => {
    try {
      const scriptArray = eval(scriptText);
      return (
        <Box sx={{ mt: 2 }}>
          {scriptArray.map((line, index) => (
            <Box key={index} sx={{ mb: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Typography variant="subtitle1" color="primary" sx={{ fontWeight: 'bold', mr: 2 }}>
                  {line[0]}:
                </Typography>
                <Button
                  size="small"
                  variant="outlined"
                  onClick={() => handleVoiceSelect(line[0])}
                >
                  Change Voice
                </Button>
              </Box>
              <Typography variant="body1" sx={{ ml: 2 }}>
                {line[1]}
              </Typography>
            </Box>
          ))}
        </Box>
      );
    } catch (e) {
      return (
        <Typography variant="body1" sx={{ whiteSpace: 'pre-wrap' }}>
          {scriptText}
        </Typography>
      );
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Typography variant="h3" component="h1" gutterBottom align="center">
        AI Podcast Generator
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <TextField
          fullWidth
          multiline
          rows={4}
          variant="outlined"
          label="Enter your podcast topic"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          sx={{ mb: 2 }}
        />
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Number of Guests</InputLabel>
          <Select
            value={numGuests}
            label="Number of Guests"
            onChange={(e) => setNumGuests(e.target.value)}
          >
            <MenuItem value={1}>1 Guest</MenuItem>
            <MenuItem value={2}>2 Guests</MenuItem>
            <MenuItem value={3}>3 Guests</MenuItem>
          </Select>
        </FormControl>
        <Button
          variant="contained"
          color="primary"
          onClick={handleGenerateScript}
          disabled={loading || !prompt}
          fullWidth
        >
          Generate Script
        </Button>
      </Paper>

      {script && (
        <Card sx={{ mb: 3 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Generated Script
            </Typography>
            {renderScript(script)}
            <Box sx={{ mt: 2 }}>
              <Button
                variant="contained"
                color="secondary"
                onClick={handleCreatePodcast}
                disabled={loading}
                fullWidth
              >
                Create Podcast
              </Button>
            </Box>
          </CardContent>
        </Card>
      )}

      {audioUrl && (
        <Card>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              Your Podcast
            </Typography>
            <audio 
              controls 
              src={audioUrl} 
              style={{ width: '100%' }}
              controlsList="nodownload"
            />
          </CardContent>
        </Card>
      )}

      {loading && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 2 }}>
          <CircularProgress />
        </Box>
      )}

      {error && (
        <Typography color="error" sx={{ mt: 2 }}>
          {error}
        </Typography>
      )}

      <Dialog open={showVoiceDialog} onClose={() => setShowVoiceDialog(false)}>
        <DialogTitle>Select Voice for {currentSpeaker}</DialogTitle>
        <DialogContent>
          <FormControl fullWidth>
            <InputLabel>Voice</InputLabel>
            <Select
              value={voiceSelections[currentSpeaker] || ''}
              label="Voice"
              onChange={(e) => handleVoiceChange(e.target.value)}
            >
              {voices.map((voice) => (
                <MenuItem key={voice.voice_id} value={voice.voice_id}>
                  {voice.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowVoiceDialog(false)}>Cancel</Button>
          <Button onClick={() => setShowVoiceDialog(false)}>Done</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
